package exercises.longestparens

import java.util.*

/*
* https://leetcode.com/problems/longest-valid-parentheses/
*/

class Solution(private val debug: Boolean = false) {
    var loops = 0

    fun longestValidParentheses(s: String): Int {
        var result = 0

        for (start in s.indices) {
            loops++
            if (s[start] == '(') {
                for (end in s.lastIndex downTo start) {
                    loops++
                    if (s[end] == ')') {
                        if (end - start < result) break
                        val candidate = s.substring(start..end)
                        if (debug) println("candidate: $candidate valid: ${candidate.validateParens()}")
                        if (candidate.validateParens() && candidate.length > result) result = candidate.length
                    }
                }
            }
        }

        return result
    }

    private fun CharSequence.validateParens(): Boolean {
        val stack = LinkedList<Char>()
        forEach {
            loops++
            when (it) {
                '(' -> stack.push(it)
                ')' -> {
                    if (stack.peek() == null) return false
                    stack.pop()
                }
                else -> return false
            }
        }

        return stack.size == 0
    }
}

class Solution2(private val debug: Boolean = false) {
    var loops = 0

    @Suppress("DuplicatedCode")
    fun longestValidParentheses(s: String): Int {
        var left = 0
        var right = 0
        var longest = 0

        s.forEach {
            loops++
            when (it) {
                '(' -> left++
                ')' -> right++
                else -> throw Exception("invalid string")
            }
            if (right > left) {
                left = 0
                right = 0
            }
            if (right == left) {
                with(right + left) {
                    if (this > longest) longest = this
                }
            }
        }

        left = 0
        right = 0

        s.toList().asReversed().forEach {
            loops++
            when (it) {
                '(' -> left++
                ')' -> right++
                else -> throw Exception("invalid string")
            }
            if (left > right) {
                left = 0
                right = 0
            }
            if (right == left) {
                with(right + left) {
                    if (this > longest) longest = this
                }
            }
        }

        return longest
    }
}

